import React, { Component } from "react";

import AceEditor from "react-ace";
import "ace-builds/src-noconflict/ext-language_tools";
import "ace-builds/src-noconflict/ext-emmet";
import "ace-builds/src-noconflict/mode-c_cpp";
import "ace-builds/src-noconflict/snippets/c_cpp";
import "ace-builds/src-noconflict/theme-dracula";

import { Slide, Button, Typography } from "@material-ui/core";

import SaveTwoToneIcon from "@material-ui/icons/SaveTwoTone";
import FolderOpenTwoToneIcon from "@material-ui/icons/FolderOpenTwoTone";
import PlayCircleFilledTwoToneIcon from "@material-ui/icons/PlayCircleFilledTwoTone";
import StopTwoToneIcon from "@material-ui/icons/StopTwoTone";

import EditorFile from "../utils/editor/files";
import GDBInstance from "../utils/gdb/index";

class CodeDrawer extends Component {
  constructor(props) {
    super(props);

    this.state = {
      data: null,
      savedDoc: null,
    };

    this.editor = React.createRef();

    this.onChange = this.onChange.bind(this);
  }

  handleOnSaveClicked = () => {
    let doc = this.editor.current.editor.session.doc.$lines;
    console.log(doc);
    this.setState(
      { ...this.state, savedDoc: doc },
      console.log("SAVED: ", this.state.savedDoc)
    );
  };

  handleOnLoadClicked = () => {
    this.editor.current.editor.session.doc.setValue(this.state.savedDoc);
  };

  onChange = (data) => {
    this.setState({ ...this.state, data: data });
    // console.log(this.state);
  };

  handleOnRunClicked = () => {
    let thisFile = new EditorFile("test", this.state.savedDoc);
    thisFile.log();
  };

  render() {
    return (
      <>
        <div
          style={{
            margin: 0,
            top: "auto",
            right: "auto",
            bottom: 20,
            left: 20,
            position: "fixed",
          }}
        >
          {this.props.open ? (
            <>
              <Button
                style={{ position: "relative", top: "auto", bottom: "auto" }}
                onClick={this.handleOnSaveClicked}
              >
                <SaveTwoToneIcon />
                <Typography variant="caption"> Save</Typography>
              </Button>

              <Button
                style={{ position: "relative", top: "auto", bottom: "auto" }}
                onClick={this.handleOnLoadClicked}
              >
                <FolderOpenTwoToneIcon />
                <Typography variant="caption">Load</Typography>
              </Button>

              <Button
                style={{ position: "relative", top: "auto", bottom: "auto" }}
                onClick={this.handleOnRunClicked}
              >
                <PlayCircleFilledTwoToneIcon />
                <Typography variant="caption">Play</Typography>
              </Button>

              <Button
                style={{ position: "relative", top: "auto", bottom: "auto" }}
                onClick={() => console.log("STOP")}
              >
                <StopTwoToneIcon />
                <Typography variant="caption">Stop</Typography>
              </Button>
            </>
          ) : null}
        </div>

        <Slide
          ref={this.editor}
          style={{ position: "fixed" }}
          direction={"up"}
          in={this.props.open}
          onExit={() => this.props.setOpen(false)}
        >
          <AceEditor
            fontSize={16}
            width="50%"
            height="75%"
            mode="c_cpp"
            theme="dracula"
            onChange={this.onChange}
            name="aceEditor"
            enableBasicAutocompletion={true}
            enableLiveAutocompletion={true}
            enableSnippets={true}
            enableEmmet={true}
            editorProps={{ $blockScrolling: true, $useWorker: true }}
          />
        </Slide>
      </>
    );
  }
}

export default CodeDrawer;
