import React, { useState } from "react";
import { Col, Row } from "react-bootstrap";

import { Button, Typography } from "@material-ui/core";

import CodeIcon from "@material-ui/icons/Code";
import ArrowForwardIosIcon from "@material-ui/icons/ArrowForwardIos";

import CodeDrawer from "./codeDrawer";
import TerminalDrawer from "./terminalDrawer";

const BottomNav = () => {
  const [openCode, setOpenCode] = useState(false);
  const [openTerm, setOpenTerm] = useState(false);

  return (
    <>
      <Row
        style={{
          zIndex: 1000,
          position: "fixed",
          top: 100,
          bottom: "auto",
          left: 10,
          right: 10,
        }}
      >
        <Col
          style={{
            zIndex: 1001,
            position: "relative",
            top: 10,
            bottom: "auto",
            left: "auto",
            right: "auto",
          }}
        >
          <CodeDrawer open={openCode} setOpen={setOpenCode} />
        </Col>
        <Col
          style={{
            zIndex: 1002,
            position: "relative",
            top: 10,
            bottom: "auto",
            left: "auto",
            right: "auto",
          }}
        >
          <TerminalDrawer open={openTerm} setOpen={setOpenTerm} />
        </Col>
      </Row>

      <div
        style={{
          zIndex: 800,
          margin: 0,
          top: "auto",
          right: 20,
          bottom: 20,
          left: "auto",
          position: "fixed",
        }}
      >
        <Button
          style={{ position: "relative", top: "auto", bottom: "auto" }}
          onClick={() => setOpenCode(!openCode)}
        >
          <CodeIcon />
          <Typography variant="caption">
            {openCode ? "Close " : "Open "}editor
          </Typography>
        </Button>

        <Button
          style={{ position: "relative", top: "auto", bottom: "auto" }}
          onClick={() => setOpenTerm(!openTerm)}
        >
          <ArrowForwardIosIcon />
          <Typography variant="caption">
            {openTerm ? "Close " : "Open "}terminal
          </Typography>
        </Button>
      </div>
    </>
  );
};

export default BottomNav;
